import React, {useState} from "react";

import {withStyles} from "@material-ui/core/styles";
import FormGroup from "@material-ui/core/FormGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";
import CheckBoxOutlineBlankIcon from "@material-ui/icons/CheckBoxOutlineBlank";
import CheckBoxIcon from "@material-ui/icons/CheckBox";
import Favorite from "@material-ui/icons/Favorite";
import FavoriteBorder from "@material-ui/icons/FavoriteBorder";
import Button from '@material-ui/core/Button';

import "./boxSelect.css";
import data from '../Data/CourseData.json'

import CardList from './cardList'
import Sem1List from './sem1List'



function BoxSelect(props) {
  const [state, setState] = React.useState({
    checkedA: false,
    checkedB: false,
    checkedC: false,
    checkedD: false,
    checkedE: false,
    checkedF: false,
    checkedG: false,
    checkedH: false,

    checkedA2: false,
    checkedB2: false,
    checkedC2: false,
    checkedD2: false,
    checkedE2: false,
    checkedF2: false,
    checkedG2: false,

    checkedA3: false,
    checkedB3: false,
    checkedC3: false,
    checkedD3: false,
    checkedE3: false,
    checkedF3: false,
    checkedG3: false,
    checkedH3: false,

    checkedA4: false,
    checkedB4: false,
    checkedC4: false,
    checkedD4: false,
    checkedE4: false,
    checkedF4: false,
    listVisibility:true
  });

  const handleChange = event => {
    setState({...state, [event.target.name]: event.target.checked});
  };

  function onShow() {
    setState({ listVisibility: true });
  }

  if (props.semester == 1)
  {
    return (
      <div className="boxSelect">

        <Sem1List state={setState}/>

        <div className="buttonContainer">
          <Button className="button" size="large" onClick={() => onShow} variant="contained" color="secondary">
            Submit
          </Button>
        </div>

        <CardList show={state.listVisibility} onNameChange={this.onShow} course={data}/>

      </div>
    );
  }



  else {
    return(
      <div className="boxSelect">
        <div className="boxSelectInput">
        <div className="boxes">
          <h2 className="boxHeader">Business</h2>
          <FormGroup className="boxesGroup">
            <FormControlLabel className="box1"
            control={<Checkbox  checked={state.checkedA3} onChange={handleChange} name="checkedA3" />}
            label={data.Courses[15].name}/>

            <FormControlLabel className="box2"
            control={<Checkbox  checked={state.checkedB3} onChange={handleChange} name="checkedB3" />}
            label={data.Courses[16].name}/>

            <FormControlLabel className="box1"
            control={<Checkbox checked={state.checkedC3} onChange={handleChange} name="checkedC3" />}
            label={data.Courses[17].name}/>

            <FormControlLabel className="box2"
            control={<Checkbox checked={state.checkedD3} onChange={handleChange} name="checkedD3" />}
            label={data.Courses[18].name}/>

            <FormControlLabel className="box1"
            control={<Checkbox checked={state.checkedE3} onChange={handleChange} name="checkedE3" />}
            label={data.Courses[19].name}/>

            <FormControlLabel className="box2"
            control={<Checkbox checked={state.checkedF3} onChange={handleChange} name="checkedF3" />}
            label={data.Courses[20].name}/>

            <FormControlLabel  className="box1"
            control={<Checkbox checked={state.checkedG3} onChange={handleChange} name="checkedG3" />}
            label={data.Courses[21].name}/>

            <FormControlLabel className="box2"
            control={<Checkbox checked={state.checkedH3} onChange={handleChange} name="checkedH3" />}
            label={data.Courses[22].name}/>
          </FormGroup>
        </div>

        <div className="boxes">
          <h2 className="boxHeader">Computer Science</h2>
          <FormGroup className="boxesGroup">
            <FormControlLabel className="box1"
            control={<Checkbox  checked={state.checkedA4} onChange={handleChange} name="checkedA4" />}
            label={data.Courses[23].name}/>

            <FormControlLabel className="box2"
            control={<Checkbox  checked={state.checkedB4} onChange={handleChange} name="checkedB4" />}
            label={data.Courses[24].name}/>

            <FormControlLabel className="box1"
            control={<Checkbox checked={state.checkedC4} onChange={handleChange} name="checkedC4" />}
            label={data.Courses[25].name}/>

            <FormControlLabel className="box2"
            control={<Checkbox checked={state.checkedD4} onChange={handleChange} name="checkedD4" />}
            label={data.Courses[26].name}/>

            <FormControlLabel className="box1"
            control={<Checkbox checked={state.checkedE4} onChange={handleChange} name="checkedE4" />}
            label={data.Courses[27].name}/>
          </FormGroup>
          </div>
        </div>
        <div className="buttonContainer">
          <Button className="button"size="large" onClick={() => { alert('clicked') }} variant="contained" color="secondary">
            Submit
          </Button>
        </div>


      </div>
    );
  }
}



export default BoxSelect;
