import React, {useState} from "react";

import {withStyles} from "@material-ui/core/styles";
import FormGroup from "@material-ui/core/FormGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";
import CheckBoxOutlineBlankIcon from "@material-ui/icons/CheckBoxOutlineBlank";
import CheckBoxIcon from "@material-ui/icons/CheckBox";
import Favorite from "@material-ui/icons/Favorite";
import FavoriteBorder from "@material-ui/icons/FavoriteBorder";
import Button from '@material-ui/core/Button';
import "./boxSelect.css";

const json = require('./Data/CourseData.json')



function BoxSelect(props) {
  const [state, setState] = React.useState({
    checkedA: false,
    checkedB: false,
    checkedC: false,
    checkedD: false,
    checkedE: false,
    checkedF: false,
    checkedG: false,
    checkedH: false,

    checkedA2: false,
    checkedB2: false,
    checkedC2: false,
    checkedD2: false,
    checkedE2: false,
    checkedF2: false,
    checkedG2: false,

    checkedA3: false,
    checkedB3: false,
    checkedC3: false,
    checkedD3: false,
    checkedE3: false,
    checkedF3: false,
    checkedG3: false,
    checkedH3: false,

    checkedA4: false,
    checkedB4: false,
    checkedC4: false,
    checkedD4: false,
    checkedE4: false,
    checkedF4: false

  });

  const handleChange = event => {
    setState({...state, [event.target.name]: event.target.checked});
  };

  let pressed = false;

  if (props.semester == 1)
  {
    return (
      <div className="boxSelect">
        <div className="boxSelectInput">
        <div className="boxes">
          <h2 className="boxHeader">Business</h2>
          <FormGroup className="boxesGroup">
            <FormControlLabel className="box1"
            control={<Checkbox  checked={state.checkedA} onChange={handleChange} name="checkedA" />}
            label="Management Accounting for Business Decisions I"/>

            <FormControlLabel className="box2"
            control={<Checkbox  checked={state.checkedB} onChange={handleChange} name="checkedB" />}
            label="Financial Accounting I"/>

            <FormControlLabel className="box1"
            control={<Checkbox checked={state.checkedC} onChange={handleChange} name="checkedC" />}
            label="Business in Society"/>

