import React from 'react'
import FormGroup from "@material-ui/core/FormGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";

import "./boxSelect.css";
import data from '../Data/CourseData.json'

function Sem1List(props) {

  const toMap1 = props.state.selected.slice(0, 8);
  const toMap2 = props.state.selected.slice(8, 15);

  function onUpdateItem(event, i) {
    props.setState(state => {
      const selected = state.selected.map((item, index) => {
        console.log(index, i);
        if (index === i.index) {
          console.log("Success?");
          return event.target.checked;
        }
        return item
      });

      return {selected};
    });

  };

  const checkbox1 = toMap1.map((checked, index) => (<FormControlLabel className={"box" + (
    index % 2 + 1)} control={<Checkbox
    checked = {
      props.state.selected[{
          index
        }]
    }
    onChange = {
      (e) => {
        onUpdateItem(e, {index})
      }
    }
    name = {
      "box" + index
    } />
} label={data.Courses[index].name} key={index}/>));

  const checkbox2 = toMap2.map((checked, index) => (<FormControlLabel className={"box" + (
    index % 2 + 1)} control= {
        <Checkbox
          checked={props.state.selected[{index}+8]}
          onChange={(e) => {onUpdateItem(e, {index} +8)}}
          name={"box" + index} />
      } label={data.Courses[index + 8].name} key={index + 8}/>));

  return (<div className="boxSelectInput">
    <div className="boxes">
      <h2 className="boxHeader">Business</h2>
      <FormGroup className="boxesGroup">
        {checkbox1}
      </FormGroup>
    </div>

    <div className="boxes">
      <h2 className="boxHeader">Computer Science</h2>
      <FormGroup className="boxesGroup">
        {checkbox2}
      </FormGroup>
    </div>
  </div>)
}

export default Sem1List
