import React from 'react'
import FormGroup from "@material-ui/core/FormGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";


import "./boxSelect.css";
import data from '../Data/CourseData.json'

function Sem1List(props) {

const [state, setState] = React.useState(props);

const handleChange = event => {
  setState({...state, [event.target.name]: event.target.checked});
};


  return (
    <div className="boxSelectInput">
    <div className="boxes">
      <h2 className="boxHeader">Business</h2>
      <FormGroup className="boxesGroup">
        <FormControlLabel className="box1"
        control={<Checkbox  checked={state.checkedA} onChange={handleChange} name="checkedA" />}
        label= {data.Courses[0].name}/>

        <FormControlLabel className="box2"
        control={<Checkbox  checked={state.checkedB} onChange={handleChange} name="checkedB" />}
        label={data.Courses[1].name}/>

        <FormControlLabel className="box1"
        control={<Checkbox checked={state.checkedC} onChange={handleChange} name="checkedC" />}
        label={data.Courses[2].name}/>

        <FormControlLabel className="box2"
        control={<Checkbox checked={state.checkedD} onChange={handleChange} name="checkedD" />}
        label= {data.Courses[3].name}/>

        <FormControlLabel className="box1"
        control={<Checkbox checked={state.checkedE} onChange={handleChange} name="checkedE" />}
        label={data.Courses[4].name}/>

        <FormControlLabel className="box2"
        control={<Checkbox checked={state.checkedF} onChange={handleChange} name="checkedF" />}
        label={data.Courses[5].name}/>

        <FormControlLabel  className="box1"
        control={<Checkbox checked={state.checkedG} onChange={handleChange} name="checkedG" />}
        label={data.Courses[6].name}/>

        <FormControlLabel className="box2"
        control={<Checkbox checked={state.checkedH} onChange={handleChange} name="checkedH" />}
        label={data.Courses[7].name}/>
      </FormGroup>
    </div>

    <div className="boxes">
      <h2 className="boxHeader">Computer Science</h2>
      <FormGroup className="boxesGroup">
        <FormControlLabel className="box1"
        control={<Checkbox  checked={state.checkedA2} onChange={handleChange} name="checkedA2" />}
        label={data.Courses[8].name}/>

        <FormControlLabel className="box2"
        control={<Checkbox  checked={state.checkedB2} onChange={handleChange} name="checkedB2" />}
        label={data.Courses[9].name}/>

        <FormControlLabel className="box1"
        control={<Checkbox checked={state.checkedC2} onChange={handleChange} name="checkedC2" />}
        label={data.Courses[10].name}/>

        <FormControlLabel className="box2"
        control={<Checkbox checked={state.checkedD2} onChange={handleChange} name="checkedD2" />}
        label={data.Courses[11].name}/>

        <FormControlLabel className="box1"
        control={<Checkbox checked={state.checkedE2} onChange={handleChange} name="checkedE2" />}
        label={data.Courses[12].name}/>

        <FormControlLabel className="box2"
        control={<Checkbox checked={state.checkedF2} onChange={handleChange} name="checkedF2" />}
        label={data.Courses[13].name}/>

        <FormControlLabel  className="box1"
        control={<Checkbox checked={state.checkedG2} onChange={handleChange} name="checkedG2" />}
        label={data.Courses[14].name}/>

      </FormGroup>
      </div>
    </div>
  )
}

export default Sem1List
