import React, {useState} from "react";

import {withStyles} from "@material-ui/core/styles";
import FormGroup from "@material-ui/core/FormGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";

import Button from '@material-ui/core/Button';

import "./boxSelect.css";
import data from '../Data/CourseData.json'

import CardList from './cardList'
import Sem1List from './sem1List'
import Sem2List from './sem2List'



function BoxSelect(props) {

  //--------------------------------------------------
  //State
  const [state, setState] = React.useState({
    selected: [
      false, false, false, false, false, false, false, false, false, false,
      false, false, false, false, false, false, false, false, false, false,
      false, false, false, false, false, false, false, false, false ],
/*    checkedA: false,
    checkedB: false,
    checkedC: false,
    checkedD: false,
    checkedE: false,
    checkedF: false,
    checkedG: false,
    checkedH: false,

    checkedA2: false,
    checkedB2: false,
    checkedC2: false,
    checkedD2: false,
    checkedE2: false,
    checkedF2: false,
    checkedG2: false,

    checkedA3: false,
    checkedB3: false,
    checkedC3: false,
    checkedD3: false,
    checkedE3: false,
    checkedF3: false,
    checkedG3: false,
    checkedH3: false,

    checkedA4: false,
    checkedB4: false,
    checkedC4: false,
    checkedD4: false,
    checkedE4: false,
    checkedF4: false,
    */
    listVisibility:false
  });


//--------------------------------------------------
//Functions

  const onShow = event => {
    setState({...state, listVisibility: true});
  }

  const onUpdateItem = i =>
  {
    props.setState(state =>
      {
      const selected = state.selected.map((item, j) =>
      {
        if (j === i)
        {
          return !item;
        }
        else
        {
          return item;
        }
      });
      return {
        selected,
      };
    });
  };





//--------------------------------------------------
//Semester 1 Checkboxes

  if (props.semester == 1)
  {
    return (
      <div className="boxSelect">

        <Sem1List state={state} setState={setState}/>

        <div className="buttonContainer">
          <Button className="button" size="large" onClick={onShow} variant="contained" color="secondary">
            Submit
          </Button>
        </div>

        <CardList show={state.listVisibility} course={data} state={state}/>

      </div>
    );
  }





  //--------------------------------------------------
  //Semester 2 Checkboxes

  else {
    return(
      <div className="boxSelect">
        <Sem2List state={state} setState={setState}/>

        <div className="buttonContainer">
          <Button className="button" size="large" onClick={onShow} variant="contained" color="secondary">
            Submit
          </Button>
        </div>

        <CardList show={state.listVisibility} course={data} state={state}/>

      </div>
    );
  }
}



export default BoxSelect;
