import React, {useState} from "react";

import {withStyles} from "@material-ui/core/styles";
import FormGroup from "@material-ui/core/FormGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";
import CheckBoxOutlineBlankIcon from "@material-ui/icons/CheckBoxOutlineBlank";
import CheckBoxIcon from "@material-ui/icons/CheckBox";
import Favorite from "@material-ui/icons/Favorite";
import FavoriteBorder from "@material-ui/icons/FavoriteBorder";
import Button from '@material-ui/core/Button';

import "./boxSelect.css";

function BoxSelect(props) {
  const [state, setState] = React.useState({
    checkedA: false,
    checkedB: false,
    checkedC: false,
    checkedD: false,
    checkedE: false,
    checkedF: false,
    checkedG: false,
    checkedH: false,

    checkedA2: false,
    checkedB2: false,
    checkedC2: false,
    checkedD2: false,
    checkedE2: false,
    checkedF2: false,
    checkedG2: false,
    checkedH: false
  });

  const handleChange = event => {
    setState({...state, [event.target.name]: event.target.checked});
  };

  if (props.semester == 1)
  {
    return (
      <div className="boxSelect">
        <div className="boxes">
          <h2 className="boxHeader">Business</h2>
          <FormGroup className="boxesGroup">
            <FormControlLabel className="box1"
            control={<Checkbox  checked={state.checkedA} onChange={handleChange} name="checkedA" />}
            label="Management Accounting for Business Decisions I"/>

            <FormControlLabel className="box2"
            control={<Checkbox  checked={state.checkedB} onChange={handleChange} name="checkedB" />}
            label="Financial Accounting I"/>

            <FormControlLabel className="box1"
            control={<Checkbox checked={state.checkedC} onChange={handleChange} name="checkedC" />}
            label="Business in Society"/>

            <FormControlLabel className="box2"
            control={<Checkbox checked={state.checkedD} onChange={handleChange} name="checkedD" />}
            label="Introduction to Fixed Income Securities & Alternative Investments"/>

            <FormControlLabel className="box1"
            control={<Checkbox checked={state.checkedE} onChange={handleChange} name="checkedE" />}
            label="Investments"/>

            <FormControlLabel className="box2"
            control={<Checkbox checked={state.checkedF} onChange={handleChange} name="checkedF" />}
            label="Contemporary Marketing Management"/>

            <FormControlLabel  className="box1"
            control={<Checkbox checked={state.checkedG} onChange={handleChange} name="checkedG" />}
            label="Taxation 1"/>

            <FormControlLabel className="box2"
            control={<Checkbox checked={state.checkedH} onChange={handleChange} name="checkedH" />}
            label="Financial Management"/>
          </FormGroup>
        </div>

        <div className="boxes">
          <h2 className="boxHeader">Computer Science</h2>
          <FormGroup className="boxesGroup">
            <FormControlLabel className="box1"
            control={<Checkbox  checked={state.checkedA2} onChange={handleChange} name="checkedA2" />}
            label="Telecommunications II"/>

            <FormControlLabel className="box2"
            control={<Checkbox  checked={state.checkedB2} onChange={handleChange} name="checkedB2" />}
            label="Symbolic Programming"/>

            <FormControlLabel className="box1"
            control={<Checkbox checked={state.checkedC2} onChange={handleChange} name="checkedC2" />}
            label="Software Engineering"/>

            <FormControlLabel className="box2"
            control={<Checkbox checked={state.checkedD2} onChange={handleChange} name="checkedD2" />}
            label="Introduction to Functional Programming"/>

            <FormControlLabel className="box1"
            control={<Checkbox checked={state.checkedE2} onChange={handleChange} name="checkedE2" />}
            label="Information Management"/>

            <FormControlLabel className="box2"
            control={<Checkbox checked={state.checkedF2} onChange={handleChange} name="checkedF2" />}
            label="Compiler Design I"/>

            <FormControlLabel  className="box1"
            control={<Checkbox checked={state.checkedG2} onChange={handleChange} name="checkedG2" />}
            label="E-Business I"/>

          </FormGroup>
        </div>


        <div className="buttonContainer">
          <Button variant="contained" color="secondary">
            Submit
          </Button>
        </div>
      </div>

          );
  }
  else {
    return(
      <h2>2</h2>
    )
  }
}

export default BoxSelect;
